mapper.select:
select ROW_NUMBER() OVER(
order by ${sidx} ${order}
order by to_sal_id desc
) AS ROW_NUM, * ,(SELECT username FROM sys_user where creator_id = user_id) as creator_name ,(SELECT username FROM sys_user where modifier_id = user_id) as modifier_name ,(SELECT customer_name FROM sal_customer where wms_transfer_order_sal.customer_code = sal_customer.customer_code) as customer_name ,(SELECT warehouse_name FROM wms_warehouse where wms_warehouse.warehouse_code = wms_transfer_order_sal.warehouse_code) as warehouse_name from wms_transfer_order_sal ORDER BY ROW_NUM
OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.update:
update wms_transfer_order_sal

	[to_date] = #{toDate}, 
	[warehouse_code] = #{warehouseCode}, 
	[customer_code] = #{customerCode}, 
	[modifier_id] = #{modifierId}, 
	[modify_date] = #{modifyDate}

where to_sal_id = #{toSalId};
update wms_transfer_order_sal_row

	[to_date] = #{toDate}, 
	[modifier_id] = #{modifierId}, 
	[modify_date] = #{modifyDate}

where to_no = #{toNo};

mapper.delete:
UPDATE wms_transfer_order_sal SET data_status = 0,modify_date = GETDATE(),modifier_id = #{modifierId} where to_sal_id = #{value};

UPDATE  wms_transfer_order_sal_row
SET data_status = 0,modify_date = GETDATE(),modifier_id = #{modifierId}
FROM      wms_transfer_order_sal AS a INNER JOIN
wms_transfer_order_sal_row ON a.to_no = wms_transfer_order_sal_row.to_no
WHERE   (a.to_sal_id = #{value})

UPDATE wms_transfer_order_sal SET data_status = 0,modify_date = GETDATE(),modifier_id = #{modifierId} where to_sal_id in

	#{toSalId}
;

UPDATE  wms_transfer_order_sal_row
SET data_status = 0,modify_date = GETDATE(),modifier_id = #{modifierId}
FROM      wms_transfer_order_sal AS a INNER JOIN
wms_transfer_order_sal_row ON a.to_no = wms_transfer_order_sal_row.to_no
WHERE   (a.to_sal_id in

	#{toPurId}
);
