mapper.select:
select ROW_NUMBER() OVER(
order by ${sidx} ${order}
order by to_id desc
) AS ROW_NUM, * ,(SELECT username FROM sys_user where creator_id = user_id) as creator_name ,(SELECT username FROM sys_user where modifier_id = user_id) as modifier_name ,(SELECT warehouse_name FROM wms_warehouse where wms_transfer_order.warehouse_code = wms_warehouse.warehouse_code) as warehouse_name ,sour_warehouse_code + ' - ' + (SELECT warehouse_name FROM wms_warehouse where wms_transfer_order.sour_warehouse_code = wms_warehouse.warehouse_code) as sour_warehouse_name ,dest_warehouse_code + ' - ' + (SELECT warehouse_name FROM wms_warehouse where wms_transfer_order.dest_warehouse_code = wms_warehouse.warehouse_code) as dest_warehouse_name from wms_transfer_order
and to_id = #{toId}
and to_date = #{toDate}
and to_no like concat('%',#{toNo},'%')
ORDER BY ROW_NUM
OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.delete:
		DELETE FROM wms_transfer_order where to_id = #{value};

		DELETE FROM wms_transfer_order_row
		FROM      wms_transfer_order AS a INNER JOIN
                wms_transfer_order_row ON a.to_no = wms_transfer_order_row.to_no
		WHERE   (a.to_id = #{value});

		DELETE FROM wms_transfer_order_sn
		FROM      wms_transfer_order AS a INNER JOIN
                wms_transfer_order_sn ON a.to_no = wms_transfer_order_sn.to_no
		WHERE   (a.to_id = #{value});

DELETE FROM wms_transfer_order where to_id in

	#{toId}
;

DELETE FROM wms_transfer_order_row
FROM      wms_transfer_order AS a INNER JOIN
wms_transfer_order_row ON a.to_no = wms_transfer_order_row.to_no
WHERE   (a.to_id in

	#{toId}
);

DELETE FROM wms_transfer_order_sn
FROM      wms_transfer_order AS a INNER JOIN
wms_transfer_order_sn ON a.to_no = wms_transfer_order_sn.to_no
WHERE   (a.to_id in

	#{toId}
);
