mapper.select:
select ROW_NUMBER() OVER(
order by a.${sidx} ${order}
order by a.order_num asc
) AS ROW_NUM, a.* ,(SELECT username FROM sys_user where a.creator_id = user_id) as creator_name ,(SELECT username FROM sys_user where a.modifier_id = user_id) as modifier_name ,(select p.dept_name from sys_dept p where p.dept_id = a.parent_dept_id) as parentDeptName from sys_dept a
${filterSql} ORDER BY ROW_NUM
OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.update:
update sys_dept
modify_date = getdate(), modifier_id = #{modifierId}, is_enabled = #{isEnabled}, parent_dept_id = #{parentDeptId}, dept_name = #{deptName}, order_num = #{orderNum}, dept_type = #{deptType},
where dept_id = #{deptId}
